            emit TokensReceived(
                message.messageId,
                message.sourceChainSelector,
                sender,
                tokenAmount.token,
                tokenAmount.amount
            );
        }
    }

    /**
     * @dev Allow the contract to receive Ether for CCIP fees
     */
    receive() external payable {}
}
